import React, {Component, Fragment} from 'react';
import {getDataCommentsAsync} from '../../redux/actions/getDataComments';
import {connect} from 'react-redux';
import InputComment from '../InputComment/InputComment';
import axios from 'axios';
import PropTypes from 'prop-types';
import './'



class PostDescription extends Component {

    state = {
        inputValueComm: '',
        newComment: {},
    }

    componentDidMount() {
        this.props.getDataComments();
    }

    onePostComments = () => {
      return this.props.dataComments.map(el => +el.postId === +this.props.id ? <li className="comments_item" key={el.id}>{el.body}</li> : null)
    }

    onePost = () => {
        return this.props.dataPosts.map(el => +el.id === +this.props.id ? 
            (<Fragment key={el.id}><h3>{el.title}</h3> <p>{el.body}</p></Fragment>)
                : null)
    }

inputChange = (event) => {
    let inputTextCom = event.target.value;
    this.setState ({
        inputValueComm: inputTextCom
    })
  };
    
      addComment = (event) => {
        let newComment = {
            postId: this.props.id,
            body: this.state.inputValueComm,
        };
        this.setState (
            {
                newComment: newComment,
                inputValueComm: '',
            }, this.postNewComment(newComment)
        )
        console.log(newComment);
      };

      postNewComment = (obj) => {
        let config = {
            headers: {
                "Content-Type": "application/json",
            },
        }
        axios.post("http://localhost:3001/comments",{postId: obj.postId, body: obj.body},config)
        .then(res => console.log(res))
        .then(() => this.props.getDataComments())
        .catch(err => console.log(err))
      }
       

    render () {
        // const {id, dataPosts, dataComments} = this.props;
        const showComments = this.onePostComments();
        const showPost = this.onePost();

    return (
        <div className="one_post_wrapper">
            <InputComment inputChange={this.inputChange} addComment={this.addComment} inputValueComm={this.state.inputValueComm} postNewComment={this.postNewComment} />
            {showPost}
            <ul>
                {showComments}
            </ul>
        </div>
        )
    }
}

const MSTP = state => ({
    dataPosts: state.dataPosts,
    dataComments: state.dataComments,
})

const MDTP = dispatch => ({
    getDataComments: () => {
        dispatch(getDataCommentsAsync())
    },
})

PostDescription.propTypes = {
    id: PropTypes.string,
}

export default connect(MSTP,MDTP)(PostDescription);
