import * as actionTypes from '../actions/type';

// const initialState = {
//     allPosts: [],
// }

const dataPosts = (state=[], action) => {
    switch(action.type) {
        case actionTypes.GET_POSTS:
        return {
            allPosts: action.data,
        };
        default:
        return state;
    }
}

export default dataPosts;
