import React from 'react';

const InputComment = () => {
    return ( <
        div >
        <
        form onSubmit = {
            this.addCity
        } >
        <
        input type = "text"
        onChange = {
            this.inputChange
        }
        value = {
            this.state.inputValue
        }
        className = "text" / >
        <
        input type = "submit"
        value = "Submit"
        className = "submit" / >
        <
        /form>

        <
        /div>
    )
}

export default InputComment;
