import React from 'react';
import {connect} from 'react-redux';

const allPosts = ({dataPosts}) => {
    return (
        <div>
            <ul>
                {dataPosts.map(el => 
                <li>{el.title}</li>)}

            </ul>
        </div>
    )
}

const MSTP = state => ({
    dataPosts: state.dataPosts,
});

export default connect(MSTP)(allPosts);
