import React from 'react';
import {connect} from 'react-redux';
import { NavLink } from 'react-router-dom';
import PropTypes from 'prop-types';
import './AllPosts.css';


const AllPosts = ({dataPosts, handleChangeId}) => {
    
    return (
        <div>
            <ul className="posts_list">
                {dataPosts.map(el => 
                <li className="posts_item" id={el.id} key={el.id} onClick={handleChangeId}>
                <NavLink className="post" to={`/posts/${el.id}`} id={el.id}>
                {el.title}
                </NavLink>
                </li>)}

            </ul>
        </div>
    )
}

const MSTP = state => ({
    dataPosts: state.dataPosts,
});

AllPosts.propTypes = {
    handleChangeId: PropTypes.func,
}

export default connect(MSTP)(AllPosts);
