import React, { Component } from 'react';
import { Switch, Route, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import App from '../../App';
import PostDescription from '../PostDescription/PostDescription';


class Root extends Component {
    state = {
        id: null,
    }

    // handleChangeId = event => {
    //     event.preventDefault();
    //     this.setState({
    //       id: event.target.id
    //     });
    //   };

    render () {
        return (
            <Switch>
                <Route exact path="/" render={props => <App 
                handleChangeId={this.handleChangeId}/>} />
                <Route exact path="/posts/:id" render={props => <PostDescription id={this.state.id} {...props}/>}/>
            </Switch>
        )
    }
}

const MSTP = state => ({
    dataPosts: state.dataPosts,
})

export default withRouter(connect(MSTP)(Root));
