import React, { Component } from 'react';
import './App.css';
import {connect} from 'react-redux';
import {getDataPostsAsync} from './redux/actions/getDataPosts';
import AllPosts from './Components/AllPosts/AllPosts';
import PropTypes from 'prop-types';




class App extends Component {

componentDidMount() {
this.props.getDataPosts();
}

  render() {
    const {handleChangeId} = this.props;
    return (
      <div className="App">
      <AllPosts handleChangeId={handleChangeId}/>

      </div>
    );
  }
}

const MDTP = dispatch => ({
  getDataPosts: () => {
    dispatch(getDataPostsAsync())
  },
})

App.propTypes = {
  handleChangeId: PropTypes.func,
}

export default connect(null, MDTP)(App);
