import React, {Component, Fragment} from 'react';
import {getDataCommentsAsync} from '../../redux/actions/getDataComments';
import {connect} from 'react-redux';
import InputComment from '../InputComment/InputComment';

class PostDescription extends Component {

    state = {
        inputValueComm: '',
    }

    componentDidMount() {
        {this.props.getDataComments()}
    }

    onePostComments = () => {
      return this.props.dataComments.map(el => +el.postId === +this.props.id ? <li>{el.body}</li> : null)
    }

    onePost = () => {
        return this.props.dataPosts.map(el => +el.id === +this.props.id ? 
            (<Fragment><h3>{el.title}</h3> <p>{el.body}</p></Fragment>)
                : null)
    }

    inputChange = (event) => {
        let inputTextCom = event.target.value;
        this.setState ({
            inputValueComm: inputTextCom
        })
      };
    
      addComment = (event) => {
        event.preventDefault();
        let newComment = {
          body: this.state.inputValue,
            id: Date.now(),
            postId: ''
        };
        // this.setState (
        //     prev => ({
        //         cities: [newCity,...prev.cities],
        //         inputValue: ''
        //     })
        // )
      };
       

    render () {
        const {id, dataPosts, dataComments} = this.props;

        const showComments = this.onePostComments();

        const showPost = this.onePost();


    return (
        <div>

            <InputComment inputChange={this.inputChange}  />

        
            {/* <h3>{dataComments.map(el => +el.postId === +id ? el.body : null)}</h3> */}
            {/* <h3>{res}</h3> */}



           {showPost}

            <ul>
                {showComments}
            </ul>
            

        </div>
    )
}
}

const MSTP = state => ({
    dataPosts: state.dataPosts,
    dataComments: state.dataComments,
})

const MDTP = dispatch => ({
    getDataComments: () => {
        dispatch(getDataCommentsAsync())
    },
})

export default connect(MSTP,MDTP)(PostDescription);
