import React from 'react';
import {connect} from 'react-redux';
import { NavLink } from 'react-router-dom';

const AllPosts = ({dataPosts, handleChangeId}) => {
    
    return (
        <div>
            <ul>
                {dataPosts.map(el => 
                <li id={el.id} key={el.id} onClick={handleChangeId}>
                <NavLink to={`/posts/${el.id}`}>
                {el.title}
                </NavLink>
                </li>)}

            </ul>
        </div>
    )
}

const MSTP = state => ({
    dataPosts: state.dataPosts,
});

export default connect(MSTP)(AllPosts);
