import {connect} from 'react-redux';
import { NavLink } from 'react-router-dom';

const AllPosts = ({dataPosts, handleChangeId}) => {
    const tr = () => {
        handleChangeId ();
    }
    return (
        <div>
            <ul>
                {dataPosts.map(el => 
                <li id={el.id} onClick={PaymentResponse.tr}>
                <NavLink to={`/posts/${el.id}`}>
                {el.title}
                </NavLink>
                </li>)}

            </ul>
        </div>
    )
}

const MSTP = state => ({
    dataPosts: state.dataPosts,
});

export default connect(MSTP)(AllPosts);
