import React, {Component} from 'react';
import {getDataCommentsAsync} from '../../redux/actions/getDataComments';
import {connect} from 'react-redux';

class PostDescription extends Component {

    componentDidMount() {
        {this.props.getDataComments()}
    }

    onePost = () => {
      return this.props.dataComments.map(el => el.postId === this.props.id ? el.body : null)
    }

    

    render () {
        const {id, dataPosts, dataComments} = this.props;

        const res = this.onePost();


    return (
        <div>
        
            <h3>{dataComments.map(el => el.postId === id ? el.body : null)}</h3>
            

        </div>
    )
}
}

const MSTP = state => ({
    dataPosts: state.dataPosts,
    dataComments: state.dataComments,
})

const MDTP = dispatch => ({
    getDataComments: () => {
        dispatch(getDataCommentsAsync())
    },
})

export default connect(MSTP,MDTP)(PostDescription);
